        "[Order Details]": {
            "columns": {
                "OrderID": {
                    "type": "INT",
                    "description": "Foreign key to orders table."
                },
                "ProductID": {
                    "type": "INT",
                    "description": "Foreign key to products table."
                },
                "UnitPrice": {
                    "type": "DECIMAL",
                    "description": "Price per unit of the product."
                },
                "Quantity": {
                    "type": "SMALLINT",
                    "description": "Quantity of the product ordered."
